	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = KpSettingPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setName($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setType($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setWidgetOptions($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setValue($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setSettingGroup($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setSettingDefault($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setSlug($arr[$keys[7]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(KpSettingPeer::DATABASE_NAME);

		if ($this->isColumnModified(KpSettingPeer::ID)) $criteria->add(KpSettingPeer::ID, $this->id);
		if ($this->isColumnModified(KpSettingPeer::NAME)) $criteria->add(KpSettingPeer::NAME, $this->name);
		if ($this->isColumnModified(KpSettingPeer::TYPE)) $criteria->add(KpSettingPeer::TYPE, $this->type);
		if ($this->isColumnModified(KpSettingPeer::WIDGET_OPTIONS)) $criteria->add(KpSettingPeer::WIDGET_OPTIONS, $this->widget_options);
		if ($this->isColumnModified(KpSettingPeer::VALUE)) $criteria->add(KpSettingPeer::VALUE, $this->value);
		if ($this->isColumnModified(KpSettingPeer::SETTING_GROUP)) $criteria->add(KpSettingPeer::SETTING_GROUP, $this->setting_group);
		if ($this->isColumnModified(KpSettingPeer::SETTING_DEFAULT)) $criteria->add(KpSettingPeer::SETTING_DEFAULT, $this->setting_default);
		if ($this->isColumnModified(KpSettingPeer::SLUG)) $criteria->add(KpSettingPeer::SLUG, $this->slug);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(KpSettingPeer::DATABASE_NAME);

		$criteria->add(KpSettingPeer::ID, $this->id);

		return $criteria;
	}

	/**
